import React, {useEffect, useState} from "react";
import Output from "./Output";


const Test = () => {
    // const arrayvalue = ["bus", "cycle"] 
    const [value,setValue] = useState();
    const [list, setList] = useState(["bus", "cycle"])
    const handleCheckbox = () => {
        // alert("value when selected: " + value)
        // let arrayJob = list;
        // setList([...arrayJob,value])
    }

    useEffect(() => {
        let arrayJob = list;
        setList([...arrayJob,value])
    },list)
    return (
        <>
            <form action="/action_page.php" >
                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike" onChange={e => setValue(e.target.value)}/>
                <label > I have a bike</label>
                <br></br>
                <input type="checkbox" id="vehicle2" name="vehicle2" value="Car" onChange={e => setValue(e.target.value)} />
                <label > I have a car</label>
                <br></br>
                <input type="checkbox" id="vehicle3" name="vehicle3" value="Boat" onChange={e => setValue(e.target.value)}/>
                <label> I have a boat</label>
                <br></br>
                <br></br>
                <input type="submit" value="Submit"  onClick={handleCheckbox}/>
            </form>
            <Output value ={list} />
        </>
    )

}

export default Test;
