import {React, useState} from "react"


const Radio = () => {
    const json = [
        {
            id: 1,
            text: "javascript"
        },
        {
            id: 2,
            text: "java"
        },
        {
            id: 3,
            text: "ReactJS"
        }
    ]
    const [checked,setData] = useState();
    // const []

    return (
        <>  
            <h1>tuan</h1>
            {json&&
                json.map(node => {
                    return (
                        <div key={node.id}>
                            <input type="radio"
                            checked = {checked !== node.id}
                            onChange={() => setData(node.id)}
                            /> 
                            {node.text}
                        </div>
                    )
                })
            }
        </>
    )
}

export default Radio
