import React, {useState} from "react";

const AddComponent = (props) => {

    const [title,setTitle] = useState("")
    const [salary,setSalary] = useState("")



    handleOnChange = (e) =>{
        this.setState ({
            [e.target.name] : e.target.value        
        })
    }
    handleClick = (event) => {
        event.preventDefault()
        // validate infor
        if (!this.state.title || !this.state.salary){
            alert("Missing input")
            return;
        }    

        this.props.addNewJobs({
            id: Math.floor(Math.random() * 1001),
            title: this.state.title,
            salary: this.state.salary
        })

        this.setState({
            title: "",
            salary: ""
        })
    }


    return (
        <>
            <div className="first">
                <label htmlFor="title">Name: </label>
                <input name = "title"  value = {title} type = "text" onChange={event =>(this.handleOnChange(event))} />
            </div>
            <div> 
                <label htmlFor="salary">Age :  </label>
                <input name = "salary" value ={salary} type = "text" onChange={event =>{this.handleOnChange(event)}}/>
            </div>
            <div>
                <button type="submit" onClick={(event) => {this.handleClick(event)}}>Submit</button>
            </div>
        </>
    )

}

export default AddComponent;
