    useEffect(()  => {
        console.log("re-render");
    }, [])

    const handleCheckbox = () => {
        alert("value when selected: " + value)
    }   

    const addListSlect = (value) => {
        setListSelect(...arrayvalue,value)
    }

    return (
        <>
            <form action="/action_page.php" >
                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike" onChange={e => setValue(e.target.value)}/>
                <label > I have a bike</label>
